		const span2 = document.getElementById('span2');
		const span3 = document.getElementById('span3');


		hamburger.addEventListener('click', () => {
			nav.classList.toggle('show', !nav.classList.contains('show'));
			hamburger.classList.toggle('show', !hamburger.classList.contains('show'));
			span1.classList.toggle('span1', !span1.classList.contains('span1'));
			span2.classList.toggle('span2', !span2.classList.contains('span2'));
